let id = 2
let pets = [
    {
        id: 0,
        name: 'Digby',
        imageURL: 'data:image/jpeg;base64,',
        age: 6,
        breed: 'Golden Retriever'
    },
    {
        id: 1,
        name: 'Goddard',
        imageURL: 'data:image/jpeg;base64,',
        age: 2,
        breed: 'Robot'
    }
]

module.exports = {
    getPets: (req, res) => res.status(200).send(pets),

    createPet: (req, res) => {
        const {name, imageURL, age, breed} = req.body

        let newPet = {
            id: id++,
            name,
            imageURL, 
            age, 
            breed
        }

        pets.push(newPet)

        res.status(200).send(pets)
    },

    deletePet: (req, res) => {
        let { id } = req.params

        let index = pets.findIndex(pet => +pet.id === +id)

        pets.splice(index, 1)

        res.status(200).send(pets)
    },

    updatePet: (req, res) => {
        let { id } = req.params
        const { name, imageURL, age, breed } = req.body
        let index = pets.findIndex(pet => +pet.id === +id)

        let updatedPet = {
            id, 
            name, 
            imageURL, 
            age, 
            breed
        }

        pets.splice(index, 1, updatedPet)
        res.status(200).send(pets)
    }
}